require.config({
  paths: {
    chart: "$services.webjars.url('org.webjars:chartjs', 'Chart.bundle.min.js')"
  }
});

require(['jquery', 'chart'], function ($) {
  const SELECTORS = {
    options: '.analytics_genericVisitsFields',
    parent: '.analytics_graphMacro',
    canvas: 'canvas',
    macroOptions: '.analytics_visitsGenericMacroOptions',
    period: '.analytics_visitsGenericPeriods',
    updateCanvas: '.analytics_visitsGenericPeriods, .analytics_genericVisitsFields'
  };
  const RGBS = [`rgb(255, 0, 0)`, `rgb(0, 255, 0)`, `rgb(0, 0, 255)`]

  // A map to store charts for dynamic updates.
  let chartsMap = new Map();

  // Listen for changes on period selector and creates a new chart with the new labels(dates).
  $(document).on('change', SELECTORS.updateCanvas, function () {
    let parent = $(this).closest(SELECTORS.parent);
    let cacheId = parent.attr('id');
    let timeInterval = parent.find(SELECTORS.macroOptions).data('date');
    let canvas = parent.find(SELECTORS.canvas);
    let yAxeNames = $('option:selected', parent.find(SELECTORS.options)).data('label').split('&');
    let endpoints = $('option:selected', parent.find(SELECTORS.options)).data('endpoints').split('&');
    let fields =parent.find(SELECTORS.options).val().split('&');
    let period = parent.find(SELECTORS.period).val()
    canvas.addClass("loading");
    editCanvas(canvas, cacheId, timeInterval, endpoints,fields ,period, yAxeNames);
  });

  // Function to create a new chart.
  function createChart(canvas, labels, values, yAxeNames) {
    let data = {
      labels: labels,
      datasets: []
    };
    yAxeNames.forEach((yAxeName, i) => {
      data.datasets.push({
        label: yAxeName,
        borderColor: RGBS[i],
        data: values[i],
        pointRadius: 0,
        pointHoverRadius: 5,
        // Makes the second line wider to be able to view the overlapping parts
        borderWidth: 2 + i*3+1
      });
    });
    return new Chart(canvas, {
      type: 'line',
      data: data,
      options: {
        scales: {
          x: {
            ticks: {
              autoSkip: true,
              maxRotation: 90,
              autoSkipPadding: 3,
              maxTicksLimit: 40
            }
          },
          y: {
            beginAtZero: true
          }
        },
        plugins: {
          tooltip: {
            mode: 'index',
            intersect: false,
          }
        },
        interaction: {
          mode: 'nearest',
          intersect: true
        }
      }
    });
  }

  function makeRequest(url, queryString) {
    return new Promise((resolve, reject) => {
      $.getJSON(`${url}?${queryString}`)
        .done(resolve)
        .fail(function (jqxhr, textStatus, error) {
          const err = `${textStatus}, ${error}`;
          console.error(`Request Failed: ${err}`);
          reject(err);
        });
    });
  }
  // Updates the chart when the labels(dates change) or are initialized.
  function editCanvas(canvas, cacheId, date, endpoints, fields, period, yAxeNames) {
    let requestPageRef = XWiki.Model.resolve('Analytics.Code.Macros.VisitsSummaryJson', XWiki.EntityType.DOCUMENT);
    let url = new XWiki.Document(requestPageRef).getURL('get');
    let requests = []; // This is correctly defined
    for (let i = 0; i < endpoints.length; i++) {
      let queryString = $.param({
        'date': date,
        'apiMethod': endpoints[i],
        'period': period
      });
      requests.push(makeRequest(url, queryString));
    }
    Promise.all(requests).then(responses => {
      let chart = chartsMap.get(cacheId);
      if (chart != undefined) {
        chart.destroy();
      }
      let values = fields.map((field, i) => responses[i][field]);
      // Map the chart to the id of the macro to be able to edit the canvas.
      chartsMap.set(cacheId, createChart(canvas, responses[0]["labels"], values, yAxeNames));
      canvas.removeClass("loading");
    }).catch(error => {
      console.error("Request failed while in the VisitsSummary macros:", error)
    })
  }

  // Initialize on document ready.
  $(document).ready(function () {
    $(".visitsOverview").each(function () {
      let cacheId = $(this).attr('id');
      let timeInterval = $(this).find(SELECTORS.macroOptions).data('date');
      let fields = $(this).find(SELECTORS.options).val().split('&');
      let yAxeNames = $('option:selected', $(this).find(SELECTORS.options)).data('label').split('&');
      let endpoints = $('option:selected', $(this).find(SELECTORS.options)).data('endpoints').split('&');
      let canvas = $(this).find(SELECTORS.canvas);
      let period = $(this).find(SELECTORS.period).val();
      editCanvas(canvas, cacheId, timeInterval, endpoints, fields, period, yAxeNames);
    });
  });
});
